import styled from 'styled-components';

export default styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    .horizontal {
        display: flex;
        justify-content: space-between;
        width: 80vw;
        margin: 40px 0 40px 0;
    }
    .vertical {
        height: 100%;
        width: 45%;
    }
    .subinfo {
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 100vw;
        background-color: yellow;
        border-top: 1px solid grey;
    }
    .caption {
        margin-bottom: 10px;
    }
    .big-text {
        font-size: 18px;
    }

`;
