import styled from 'styled-components';

export default styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    .columns {
        width: 80vw;
    }
    .container {
        border: 1px solid black;
        width: 38vw;
    }
    #welcome {
        margin-bottom: 1.5rem;
        font-size: 2rem;
    }
    #caption {
        margin-bottom: 1rem;
    }
`;
